import React from 'react';
import { Link } from 'react-router-dom';
import { ArrowLeft, AlertCircle, Phone } from 'lucide-react';
import { useLanguage } from '../context/LanguageContext';
import { useData } from '../context/DataContext';
import DriverCard from '../components/DriverCard';

const EmergencyPage: React.FC = () => {
  const { t } = useLanguage();
  const { getEmergencyDrivers, stages } = useData();
  
  const emergencyDrivers = getEmergencyDrivers();

  return (
    <div className="emergency-page">
      <header className="emergency-header">
        <div className="container">
          <Link to="/" className="back-btn">
            <ArrowLeft size={20} />
          </Link>
          
          <div className="emergency-title">
            <AlertCircle className="emergency-icon" size={32} />
            <div>
              <h1>{t('emergencyAutos')}</h1>
              <p>24/7 Available for Hospital & Emergency Rides</p>
            </div>
          </div>
        </div>
      </header>

      <main className="main-content">
        <div className="container">
          {/* Emergency Notice */}
          <div className="emergency-notice">
            <div className="notice-content">
              <Phone className="notice-icon" size={24} />
              <div>
                <h3>For Medical Emergencies</h3>
                <p>These drivers are specially available for hospital visits and emergency transportation</p>
              </div>
            </div>
          </div>

          {/* Emergency Drivers */}
          {emergencyDrivers.length > 0 ? (
            <section className="emergency-drivers-list">
              <h2>Available Emergency Drivers ({emergencyDrivers.length})</h2>
              <div className="drivers-grid">
                {emergencyDrivers.map((driver) => {
                  const stage = stages.find(s => s.id === driver.stageId);
                  return (
                    <DriverCard 
                      key={driver.id} 
                      driver={driver} 
                      stage={stage}
                      showStage={true}
                    />
                  );
                })}
              </div>
            </section>
          ) : (
            <div className="no-emergency-drivers">
              <AlertCircle size={48} />
              <h3>No Emergency Drivers Available</h3>
              <p>Please try regular drivers or contact admin.</p>
              <Link to="/" className="btn btn-primary">
                <ArrowLeft size={18} />
                Back to Home
              </Link>
            </div>
          )}

          {/* Emergency Instructions */}
          <section className="emergency-instructions">
            <h3>Emergency Guidelines</h3>
            <ul>
              <li>üè• These drivers prioritize hospital and medical emergency trips</li>
              <li>üìû Call directly for fastest response</li>
              <li>‚ö° Available 24/7 for urgent transportation needs</li>
              <li>üí≥ Payment can be discussed with the driver</li>
            </ul>
          </section>
        </div>
      </main>
    </div>
  );
};

export default EmergencyPage;
