import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { ArrowLeft, MapPin, Users } from 'lucide-react';
import { useLanguage } from '../context/LanguageContext';
import { useData } from '../context/DataContext';
import DriverCard from '../components/DriverCard';
import SearchBar from '../components/SearchBar';

const StagePage: React.FC = () => {
  const { stageId } = useParams<{ stageId: string }>();
  const { t, language } = useLanguage();
  const { stages, getDriversByStage, searchDrivers } = useData();
  const [filteredDrivers, setFilteredDrivers] = useState<any[]>([]);
  const [isSearching, setIsSearching] = useState(false);

  const stage = stages.find(s => s.id === stageId);
  const allDrivers = stageId ? getDriversByStage(stageId) : [];
  const stageName = stage ? (language === 'kn' ? stage.nameKn : stage.name) : '';

  const handleSearch = (query: string) => {
    if (query.trim()) {
      setIsSearching(true);
      // Search within this stage's drivers
      const results = allDrivers.filter(driver =>
        driver.name.toLowerCase().includes(query.toLowerCase()) ||
        driver.phoneNumber.includes(query) ||
        driver.vehicleNumber.toLowerCase().includes(query.toLowerCase())
      );
      setFilteredDrivers(results);
    } else {
      setIsSearching(false);
      setFilteredDrivers([]);
    }
  };

  if (!stage) {
    return (
      <div className="stage-page">
        <div className="container">
          <div className="error-message">
            <h2>Stage not found</h2>
            <Link to="/" className="btn btn-primary">
              <ArrowLeft size={18} />
              Go Home
            </Link>
          </div>
        </div>
      </div>
    );
  }

  const driversToShow = isSearching ? filteredDrivers : allDrivers;
  const emergencyDrivers = driversToShow.filter(d => d.isEmergency);
  const regularDrivers = driversToShow.filter(d => !d.isEmergency);

  return (
    <div className="stage-page">
      <header className="stage-header">
        <div className="container">
          <Link to="/" className="back-btn">
            <ArrowLeft size={20} />
          </Link>
          
          <div className="stage-title">
            <div className="stage-icon">
              <MapPin size={28} />
            </div>
            <div>
              <h1>{stageName}</h1>
              <div className="stage-stats">
                <Users size={16} />
                <span>{allDrivers.length} {t('availableNow')}</span>
              </div>
            </div>
          </div>
        </div>
      </header>

      <main className="main-content">
        <div className="container">
          {/* Search within stage */}
          <SearchBar 
            onSearch={handleSearch} 
            placeholder={`Search drivers in ${stageName}...`}
          />

          {/* Emergency Drivers */}
          {emergencyDrivers.length > 0 && (
            <section className="emergency-drivers-section">
              <h2 className="section-title emergency">
                <span className="emergency-badge">{t('emergency')}</span>
                Emergency Drivers
              </h2>
              <div className="drivers-grid">
                {emergencyDrivers.map((driver) => (
                  <DriverCard key={driver.id} driver={driver} stage={stage} />
                ))}
              </div>
            </section>
          )}

          {/* Regular Drivers */}
          {regularDrivers.length > 0 && (
            <section className="regular-drivers-section">
              <h2 className="section-title">
                Available Drivers
                {emergencyDrivers.length > 0 && ` (${regularDrivers.length})`}
              </h2>
              <div className="drivers-grid">
                {regularDrivers.map((driver) => (
                  <DriverCard key={driver.id} driver={driver} stage={stage} />
                ))}
              </div>
            </section>
          )}

          {/* No drivers found */}
          {driversToShow.length === 0 && (
            <div className="no-drivers">
              {isSearching ? (
                <p>{t('noDriversFound')}</p>
              ) : (
                <div className="empty-state">
                  <Users size={48} />
                  <h3>No drivers available in {stageName}</h3>
                  <p>Please try another stage or check back later.</p>
                  <Link to="/" className="btn btn-primary">
                    <ArrowLeft size={18} />
                    Back to Home
                  </Link>
                </div>
              )}
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default StagePage;
