import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { MapPin } from 'lucide-react';
import { useLanguage } from '../context/LanguageContext';
import { useData } from '../context/DataContext';
import SearchBar from '../components/SearchBar';
import StageList from '../components/StageList';
import DriverCard from '../components/DriverCard';
import MapView from '../components/MapView';

const HomePage: React.FC = () => {
  const { t } = useLanguage();
  const { stages, searchDrivers } = useData();
  const [searchResults, setSearchResults] = useState<any[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  
  const handleSearch = (query: string) => {
    if (query.trim()) {
      setIsSearching(true);
      const results = searchDrivers(query);
      setSearchResults(results);
    } else {
      setIsSearching(false);
      setSearchResults([]);
    }
  };

  return (
    <div className="homepage">
      <header className="App-header">
        <h1 className="App-title">{t('villageAutoConnect')}</h1>
        <p className="App-subtitle">{t('findAutoInstantly')}</p>
      </header>

      <main className="main-content">
        <div className="container">
          {/* Search Section */}
          <section className="search-section">
            <SearchBar onSearch={handleSearch} />
          </section>

          {/* Search Results */}
          {isSearching && (
            <section className="search-results">
              <h2>{t('searchResults')}</h2>
              {searchResults.length > 0 ? (
                <div className="drivers-grid">
                  {searchResults.map((driver) => {
                    const stage = stages.find(s => s.id === driver.stageId);
                    return (
                      <DriverCard 
                        key={driver.id} 
                        driver={driver} 
                        stage={stage}
                        showStage={true}
                      />
                    );
                  })}
                </div>
              ) : (
                <p className="no-results">{t('noDriversFound')}</p>
              )}
            </section>
          )}

          {!isSearching && (
            <>
              {/* Stages Section */}
              <section className="stages-section">
                <h2>{t('selectStage')}</h2>
                <StageList />
              </section>

              {/* Map Section */}
              <section className="map-section">
                <h2>
                  <MapPin size={20} />
                  Village Map
                </h2>
                <MapView stages={stages} />
              </section>
            </>
          )}
        </div>
      </main>
    </div>
  );
};

export default HomePage;
